import { Chart } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarController,
  BarElement,
  CandlestickController,
  CandlestickElement,
  TimeScale,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarController,
  BarElement,
  CandlestickController,
  CandlestickElement,
  TimeScale,
  Tooltip,
  Legend
);

const CandlestickChart = ({ data, symbol, darkMode }) => {
  const chartData = {
    labels: data.map((item) => item.date),
    datasets: [
      {
        label: `${symbol} Candlestick`,
        data: data.map((item) => ({
          x: item.date,
          o: item.open,
          h: item.high,
          l: item.low,
          c: item.close
        })),
        color: {
          up: darkMode ? '#10B981' : '#059669',
          down: darkMode ? '#EF4444' : '#DC2626',
          unchanged: darkMode ? '#9CA3AF' : '#6B7280',
        },
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          color: darkMode ? '#E5E7EB' : '#111827',
        }
      },
    },
    scales: {
      x: {
        grid: {
          color: darkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)',
        },
        ticks: {
          color: darkMode ? '#9CA3AF' : '#6B7280',
        }
      },
      y: {
        grid: {
          color: darkMode ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)',
        },
        ticks: {
          color: darkMode ? '#9CA3AF' : '#6B7280',
        }
      },
    },
  };

  return <Chart type="candlestick" data={chartData} options={options} />;
};

export default CandlestickChart;
