import { useState, useEffect } from 'react';

export const useFinancialData = (symbol = 'IBM') => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);
        
        // ðŸ‘‡ Replace with your actual API key
        const API_KEY = 'Y1QSGX9GEK8V00WOA'; 
        const response = await fetch(
          `https://www.alphavantage.co/query?function=TIME_SERIES_DAILY&symbol=${symbol}&apikey=${API_KEY}`
        );
        
        if (!response.ok) throw new Error('Network error');
        
        const result = await response.json();
        
        // Handle API errors
        if (result['Error Message']) {
          throw new Error(result['Error Message']);
        }
        if (result.Note && result.Note.includes('API call frequency')) {
          throw new Error('API limit reached - try again soon');
        }

        const timeSeries = result['Time Series (Daily)'];
        if (!timeSeries) throw new Error('No market data found');

        // Format data for charts
        const formattedData = Object.entries(timeSeries)
          .slice(0, 30) // Last 30 days
          .map(([date, values]) => ({
            date,
            open: parseFloat(values['1. open']),
            high: parseFloat(values['2. high']),
            low: parseFloat(values['3. low']),
            close: parseFloat(values['4. close']),
            volume: parseInt(values['5. volume'])
          }))
          .reverse(); // Newest first

        setData(formattedData);
      } catch (err) {
        setError(err.message);
        console.error('API Error:', err);
        setData(getSampleData(symbol)); // Fallback to realistic sample data
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [symbol]);

  return { data, loading, error };
};

// Realistic sample data generator
function getSampleData(symbol) {
  const baseValues = {
    'AAPL': { price: 180, volatility: 5 },
    'MSFT': { price: 300, volatility: 7 },
    'IBM': { price: 140, volatility: 3 },
    'BTC': { price: 50000, volatility: 2000 },
    'default': { price: 100, volatility: 2 }
  };

  const { price, volatility } = baseValues[symbol] || baseValues.default;

  return Array.from({ length: 30 }, (_, i) => {
    const date = new Date();
    date.setDate(date.getDate() - (29 - i));
    
    const randomChange = (Math.random() - 0.5) * volatility;
    const open = price + randomChange;
    const close = open + (Math.random() - 0.5) * volatility;

    return {
      date: date.toISOString().split('T')[0],
      open: parseFloat(open.toFixed(2)),
      high: parseFloat((Math.max(open, close) + Math.random() * volatility/3).toFixed(2)),
      low: parseFloat((Math.min(open, close) - Math.random() * volatility/3).toFixed(2)),
      close: parseFloat(close.toFixed(2)),
      volume: Math.floor(Math.random() * 5000000) + 1000000
    };
  });
}
